import React, { useState } from "react";
import Webcam from "react-webcam";

function App() {
  const [message, setMessage] = useState("");
  const [loading, setLoading] = useState(false);

  const startAttendance = async () => {
    setLoading(true);
    setMessage("Starting attendance...");

    try {
      const response = await fetch("http://127.0.0.1:8000/api/start_attendance/", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
      });

      const data = await response.json();

      if (data.status === "success") {
        setMessage(`✅ ${data.name} — Attendance Marked`);
      } else {
        setMessage(`❌ ${data.message}`);
      }
    } catch (error) {
      setMessage("Error connecting to backend");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{ textAlign: "center", marginTop: "50px" }}>
      <h1>Face Attendance System</h1>
      <Webcam
        audio={false}
        height={360}
        width={480}
        screenshotFormat="image/jpeg"
        style={{ borderRadius: "12px", boxShadow: "0 0 10px gray" }}
      />
      <br />
      <button
        onClick={startAttendance}
        disabled={loading}
        style={{
          marginTop: "20px",
          padding: "10px 20px",
          fontSize: "18px",
          cursor: "pointer",
          borderRadius: "8px",
        }}
      >
        {loading ? "Detecting..." : "Start Attendance"}
      </button>
      <h2 style={{ marginTop: "20px", color: "green" }}>{message}</h2>
      <p style={{ marginTop: "10px", color: "gray" }}>Press "Q" to quit the camera window</p>
    </div>
  );
}

export default App;
