import { useNavigate } from "react-router-dom";

export default function Home() {
  const navigate = useNavigate();

  return (
    <div className="min-h-screen bg-gradient-to-r from-purple-500 via-pink-500 to-red-500 flex flex-col items-center justify-center p-6 transition-all duration-500">
      {/* Dashboard Title */}
      <h1 className="text-5xl font-extrabold mb-12 text-white drop-shadow-2xl text-center">
        Face Attendance System
      </h1>

      {/* Action Cards */}
      <div className="grid grid-cols-1 sm:grid-cols-2 gap-8 w-full max-w-4xl">
        {/* Register Card */}
        <div
          onClick={() => navigate("/register")}
          className="cursor-pointer bg-white rounded-2xl shadow-2xl p-8 flex flex-col items-center justify-center text-center transition transform hover:-translate-y-2 hover:scale-105 hover:shadow-2xl hover:bg-gradient-to-br hover:from-purple-400 hover:via-pink-400 hover:to-red-400 hover:text-white"
        >
          <h2 className="text-2xl font-bold mb-4">Register User</h2>
          <p className="text-gray-600 hover:text-white">
            Add a new user with photo and department details to the system.
          </p>
        </div>

        {/* Attendance Card */}
        <div
          onClick={() => navigate("/attendance")}
          className="cursor-pointer bg-white rounded-2xl shadow-2xl p-8 flex flex-col items-center justify-center text-center transition transform hover:-translate-y-2 hover:scale-105 hover:shadow-2xl hover:bg-gradient-to-br hover:from-purple-400 hover:via-pink-400 hover:to-red-400 hover:text-white"
        >
          <h2 className="text-2xl font-bold mb-4">Mark Attendance</h2>
          <p className="text-gray-600 hover:text-white">
            Record attendance using face recognition through your webcam.
          </p>
        </div>
      </div>
    </div>
  );
}
