import React, { useState, useEffect } from "react";
import axios from "axios";

function RegisterUser() {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [department, setDepartment] = useState("");
  const [departments, setDepartments] = useState([]);
  const [selectedFile, setSelectedFile] = useState(null);
  const [message, setMessage] = useState("");

  // Fetch departments for dropdown
  useEffect(() => {
    axios.get("http://127.0.0.1:8000/departments/")
      .then(res => setDepartments(res.data))
      .catch(err => console.error(err));
  }, []);

  const handleFileChange = (e) => {
    setSelectedFile(e.target.files[0]);
  };

  const handleSubmit = async () => {
    if (!name || !email || !selectedFile) {
      setMessage("Please fill all required fields and select a photo.");
      return;
    }

    const formData = new FormData();
    formData.append("name", name);
    formData.append("email", email);
    formData.append("department", department);
    formData.append("photo", selectedFile);

    try {
      const res = await axios.post(
        "http://127.0.0.1:8000/api/register/",
        formData,
        { headers: { "Content-Type": "multipart/form-data" } }
      );

      setMessage(res.data.message);
    } catch (err) {
      if (err.response) setMessage(err.response.data.error || err.response.data.warning);
      else setMessage("Registration failed. Try again.");
    }
  };

  return (
    <div className="p-4 max-w-md mx-auto">
      <h2 className="text-xl font-bold mb-4">Register User</h2>

      <div className="mb-2">
        <label>Name:</label>
        <input
          type="text"
          value={name}
          onChange={e => setName(e.target.value)}
          className="border p-1 w-full"
        />
      </div>

      <div className="mb-2">
        <label>Email:</label>
        <input
          type="email"
          value={email}
          onChange={e => setEmail(e.target.value)}
          className="border p-1 w-full"
        />
      </div>

      <div className="mb-2">
        <label>Department:</label>
        <select
          value={department}
          onChange={e => setDepartment(e.target.value)}
          className="border p-1 w-full"
        >
          <option value="">Select Department</option>
          {departments.map(d => (
            <option key={d.department_id} value={d.department_id}>{d.department_name}</option>
          ))}
        </select>
      </div>

      <div className="mb-2">
        <label>Photo (PNG/JPG):</label>
        <input type="file" accept="image/png, image/jpeg" onChange={handleFileChange} />
      </div>

      <button
        onClick={handleSubmit}
        className="bg-blue-600 text-white p-2 rounded mt-2"
      >
        Register
      </button>

      {message && <p className="mt-3 text-green-600">{message}</p>}
    </div>
  );
}

export default RegisterUser;
