import React, { useRef, useEffect, useState } from "react";

const AttendancePage = () => {
  const videoRef = useRef(null);
  const [status, setStatus] = useState(""); // for showing live message below video

  // Start webcam stream
  useEffect(() => {
    const startCamera = async () => {
      try {
        const stream = await navigator.mediaDevices.getUserMedia({ video: true });
        if (videoRef.current) {
          videoRef.current.srcObject = stream;
        }
      } catch (err) {
        console.error("Error accessing webcam:", err);
        setStatus("‚ö†Ô∏è Cannot access webcam. Please allow camera permission.");
      }
    };
    startCamera();
  }, []);

  // Capture image and send to backend
  const captureAndMark = async () => {
    setStatus("‚è≥ Processing... please wait");

    try {
      const video = videoRef.current;
      if (!video) {
        setStatus("‚ö†Ô∏è No video stream detected.");
        return;
      }

      // Capture frame
      const canvas = document.createElement("canvas");
      canvas.width = video.videoWidth;
      canvas.height = video.videoHeight;
      const ctx = canvas.getContext("2d");
      ctx.drawImage(video, 0, 0, canvas.width, canvas.height);
      const imageData = canvas.toDataURL("image/jpeg", 0.9); // high quality

      // Send to backend
      const response = await fetch("http://127.0.0.1:8000/api/start/", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ image: imageData }),
      });

      if (!response.ok) {
        setStatus("‚ùå Server error. Please check Django logs.");
        return;
      }

      const result = await response.json();
      console.log("Backend response:", result);

      if (result.status === "success") {
        setStatus(`‚úÖ ${result.message}`);
      } else if (result.status === "failed") {
        setStatus("‚ùå Face not recognised. Try again.");
      } else {
        setStatus("‚ö†Ô∏è Unexpected response from server.");
      }
    } catch (error) {
      console.error("Fetch error:", error);
      setStatus("‚ùå Could not connect to backend. Make sure Django is running.");
    }
  };

  return (
    <div style={styles.container}>
      <h2 style={styles.title}>üéØ Face Attendance System</h2>
      <video ref={videoRef} autoPlay width="400" height="300" style={styles.video} />
      <br />
      <button onClick={captureAndMark} style={styles.button}>
        Mark Attendance
      </button>
      <p style={styles.status}>{status}</p>
    </div>
  );
};

// --------------------
// Inline CSS styles
// --------------------
const styles = {
  container: {
    textAlign: "center",
    marginTop: "40px",
    fontFamily: "Arial, sans-serif",
  },
  title: {
    color: "#333",
    marginBottom: "20px",
  },
  video: {
    borderRadius: "10px",
    border: "2px solid #555",
  },
  button: {
    marginTop: "20px",
    padding: "10px 20px",
    backgroundColor: "#007BFF",
    color: "white",
    border: "none",
    borderRadius: "6px",
    cursor: "pointer",
    fontSize: "16px",
  },
  status: {
    marginTop: "15px",
    fontSize: "18px",
    fontWeight: "bold",
  },
};

export default AttendancePage;
