import React, { useState } from "react";

const AttendanceButton = () => {
  const [message, setMessage] = useState("");

  const handleStartAttendance = async () => {
    setMessage("‚è≥ Starting camera...");
    try {
      const response = await fetch("http://127.0.0.1:8000/api/start_attendance/", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
      });

      const data = await response.json();
      setMessage(data.name ? `‚úÖ ${data.name} - ${data.status}` : data.status);
    } catch (error) {
      console.error("Error:", error);
      setMessage("‚ùå Error connecting to backend.");
    }
  };

  return (
    <div style={{ textAlign: "center", marginTop: "50px" }}>
      <h2>üé• Face Attendance System</h2>
      <button
        onClick={handleStartAttendance}
        style={{
          backgroundColor: "#4CAF50",
          color: "white",
          padding: "12px 24px",
          border: "none",
          borderRadius: "8px",
          cursor: "pointer",
        }}
      >
        Start Attendance
      </button>
      <p style={{ marginTop: "20px", fontSize: "18px" }}>{message}</p>
    </div>
  );
};

export default AttendanceButton;
