import { useState, useRef } from "react";
import Webcam from "react-webcam";
import axios from "axios";

export default function Attendance() {
  const webcamRef = useRef(null);
  const [status, setStatus] = useState("");
  const [loading, setLoading] = useState(false);

  const captureAndSend = async () => {
    setLoading(true);
    setStatus("");

    const imageSrc = webcamRef.current.getScreenshot();
    const email = localStorage.getItem("email"); // logged-in user's email

    try {
      const res = await axios.post("http://127.0.0.1:8000/api/start/", {
        image: imageSrc,
      });

      if (res.data.status === "success") {
        setStatus(`✅ Attendance marked for ${res.data.name}`);
      } else if (res.data.status === "exists") {
        setStatus(`⚠️ ${res.data.message}`);
      } else {
        setStatus("❌ Face not recognised. Try again.");
      }
    } catch (err) {
      setStatus("❌ Error marking attendance");
    }

    setLoading(false);
  };

  return (
    <div className="min-h-screen bg-gray-100 flex flex-col items-center p-6">
      <h2 className="text-3xl font-bold mb-6 text-blue-600">Mark Attendance</h2>

      <div className="bg-white p-4 rounded shadow-lg flex flex-col items-center">
        <Webcam
          audio={false}
          ref={webcamRef}
          screenshotFormat="image/jpeg"
          className="rounded border w-80 h-60 object-cover mb-4"
        />
        <button
          onClick={captureAndSend}
          disabled={loading}
          className="bg-blue-600 text-white px-6 py-3 rounded shadow hover:bg-blue-700 transition"
        >
          {loading ? "Processing..." : "Mark Attendance"}
        </button>

        {status && (
          <p className={`mt-4 font-semibold ${status.includes("✅") ? "text-green-600" : "text-red-600"}`}>
            {status}
          </p>
        )}
      </div>
    </div>
  );
}
