import React, { useRef, useState } from "react";
import axios from "axios";

function App() {
  const videoRef = useRef();
  const canvasRef = useRef();
  const [message, setMessage] = useState("");
  const [stream, setStream] = useState(null); // store media stream

  // Start webcam
  const startWebcam = async () => {
    try {
      const mediaStream = await navigator.mediaDevices.getUserMedia({ video: true });
      videoRef.current.srcObject = mediaStream;
      setStream(mediaStream);
    } catch (err) {
      console.error("Error starting webcam:", err);
      setMessage("Cannot access webcam");
    }
  };

  // Stop webcam
  const stopWebcam = () => {
    if (stream) {
      stream.getTracks().forEach((track) => track.stop());
      videoRef.current.srcObject = null;
      setStream(null);
      setMessage("Webcam stopped");
    }
  };

  // Capture frame and send to Django
  const captureAndSend = async () => {
    if (!videoRef.current) return;

    const canvas = canvasRef.current;
    const video = videoRef.current;

    canvas.width = video.videoWidth;
    canvas.height = video.videoHeight;
    canvas.getContext("2d").drawImage(video, 0, 0, canvas.width, canvas.height);

    const imgBase64 = canvas.toDataURL("image/jpeg");

    try {
      const response = await axios.post(
        "http://127.0.0.1:8000/api/start_attendance/",
        { image: imgBase64 }
      );
      if (response.data.marked.length > 0) {
        setMessage(`${response.data.marked.join(", ")} attendance marked!`);
      } else {
        setMessage("No face recognized or already marked.");
      }
    } catch (err) {
      console.error(err);
      setMessage("Error connecting to backend.");
    }
  };

  return (
    <div style={{ textAlign: "center", marginTop: "20px" }}>
      <h1>Face Attendance System</h1>
      <video
        ref={videoRef}
        autoPlay
        style={{ width: "400px", border: "1px solid black" }}
      ></video>
      <canvas ref={canvasRef} style={{ display: "none" }}></canvas>
      <div style={{ marginTop: "10px" }}>
        <button onClick={startWebcam} style={{ marginRight: "10px" }}>
          Start Webcam
        </button>
        <button onClick={stopWebcam} style={{ marginRight: "10px" }}>
          Stop Webcam
        </button>
        <button onClick={captureAndSend}>Mark Attendance</button>
      </div>
      <p style={{ marginTop: "10px", fontWeight: "bold" }}>{message}</p>
    </div>
  );
}

export default App;
