// ✅ AttendancePage.js
import React, { useRef, useEffect } from "react";

const AttendancePage = () => {
  const videoRef = useRef();

  useEffect(() => {
    navigator.mediaDevices.getUserMedia({ video: true }).then(stream => {
      videoRef.current.srcObject = stream;
    });
  }, []);

  const captureAndMark = async () => {
    const canvas = document.createElement("canvas");
    canvas.width = videoRef.current.videoWidth;
    canvas.height = videoRef.current.videoHeight;
    canvas.getContext("2d").drawImage(videoRef.current, 0, 0);
    const imageData = canvas.toDataURL("image/jpeg");

    const response = await fetch("http://127.0.0.1:8000/api/start/", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ image: canvas.toDataURL('image/jpeg') }),
    });

    const result = await response.json();
    alert(result.message);
  };

  return (
    <div className="attendance-container">
      <h2>Face Attendance</h2>
      <video ref={videoRef} autoPlay width="400" height="300" />
      <br />
      <button onClick={captureAndMark}>Mark Attendance</button>
    </div>
  );
};

export default Attendance; // ✅ important
