import React, { useRef, useState } from "react";
import axios from "axios";

function App() {
  const videoRef = useRef();
  const canvasRef = useRef();
  const [message, setMessage] = useState("");

  // Start webcam
  const startWebcam = () => {
    navigator.mediaDevices
      .getUserMedia({ video: true })
      .then((stream) => {
        videoRef.current.srcObject = stream;
      });
  };

  // Capture frame and send to Django
  const captureAndSend = async () => {
    const canvas = canvasRef.current;
    const video = videoRef.current;

    canvas.width = video.videoWidth;
    canvas.height = video.videoHeight;
    canvas.getContext("2d").drawImage(video, 0, 0, canvas.width, canvas.height);

    const imgBase64 = canvas.toDataURL("image/jpeg");

    try {
      const response = await axios.post("http://127.0.0.1:8000/api/start_attendance/", { image: imgBase64 });
      if (response.data.marked.length > 0) {
        setMessage(`${response.data.marked.join(", ")} attendance marked!`);
      } else {
        setMessage("No face recognized or already marked.");
      }
    } catch (err) {
      console.error(err);
      setMessage("Error connecting to backend.");
    }
  };

  return (
    <div>
      <h1>Face Attendance</h1>
      <video ref={videoRef} autoPlay style={{ width: "400px" }}></video>
      <canvas ref={canvasRef} style={{ display: "none" }}></canvas>
      <div>
        <button onClick={startWebcam}>Start Webcam</button>
        <button onClick={captureAndSend}>Mark Attendance</button>
      </div>
      <p>{message}</p>
    </div>
  );
}

export default App;
