import { useEffect, useState } from "react";
import { getDepartments } from "../api/api";

function DepartmentList() {
  const [departments, setDepartments] = useState([]);

  useEffect(() => {
    getDepartments().then(res => setDepartments(res.data));
  }, []);

  return (
    <div className="container" style={{ marginLeft: 250, paddingTop: 20 }}>
      <div className="container-card">
        <h2 className="mb-3">Departments</h2>

        <a href="/departments/add" className="btn btn-success btn-custom mb-3">
          + Add Department
        </a>

        <table className="table table-striped">
          <thead>
            <tr>
              <th>ID</th>
              <th>Name</th>
              <th>Location</th>
              <th>Total Employees</th>
            </tr>
          </thead>

          <tbody>
            {departments.map(d => (
              <tr key={d.department_id}>
                <td>{d.department_id}</td>
                <td>{d.department_name}</td>
                <td>{d.location}</td>
                <td>{d.total_employees}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default DepartmentList;
