import { useState, useRef } from "react";
import Webcam from "react-webcam";
import axios from "axios";

export default function Attendance() {
  const webcamRef = useRef(null);
  const [status, setStatus] = useState("");
  const [loading, setLoading] = useState(false);

  const captureAndSend = async () => {
    setLoading(true);
    setStatus("");

    const imageSrc = webcamRef.current.getScreenshot();

    if (!imageSrc) {
      setStatus("❌ Webcam not ready. Please allow camera access.");
      setLoading(false);
      return;
    }

    const base64Data = imageSrc.split(",")[1];

    try {
      const res = await axios.post(
        "http://127.0.0.1:8000/api/start/",
        { image: base64Data },
        { headers: { "Content-Type": "application/json" } }
      );

      if (res.data.status === "success") {
        setStatus(`✅ Attendance marked for ${res.data.name}`);
      } else if (res.data.status === "exists") {
        setStatus(`⚠️ ${res.data.message}`);
      } else {
        setStatus("❌ Face not recognised. Try again.");
      }
    } catch (err) {
      console.error(err.response || err);
      setStatus("❌ Error marking attendance");
    }

    setLoading(false);
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-pink-50 via-orange-50 to-yellow-50 flex flex-col items-center justify-center p-6">
      <h2 className="text-4xl font-extrabold mb-10 text-pink-700 drop-shadow-md text-center">
        Mark Your Attendance
      </h2>

      <div className="bg-white rounded-3xl shadow-2xl p-8 flex flex-col items-center w-full max-w-md border-t-8 border-pink-400">
        {/* Webcam Box */}
        <div className="relative w-80 h-60 mb-6 rounded-xl overflow-hidden border-4 border-dashed border-pink-200 shadow-inner">
          <Webcam
            audio={false}
            ref={webcamRef}
            screenshotFormat="image/jpeg"
            className="w-full h-full object-cover"
          />
          <div className="absolute inset-0 border-2 border-pink-400 rounded-xl pointer-events-none"></div>
        </div>
        


        {/* Centered Button */}
        <div className="w-full flex justify-center">
          <button
            onClick={captureAndSend}
            disabled={loading}
            className={`w-3/4 py-3 rounded-xl font-semibold text-white text-lg transition-all duration-300 shadow-lg ${
              loading
                ? "bg-pink-300 cursor-not-allowed"
                : "bg-gradient-to-r from-pink-500 via-orange-400 to-yellow-400 hover:from-pink-600 hover:via-orange-500 hover:to-yellow-500"
            }`}
          >
            {loading ? "Processing..." : "Mark Attendance"}
          </button>
        </div>

        {/* Status Message */}
        {status && (
          <p
            className={`mt-6 text-center text-lg font-medium ${
              status.includes("✅")
                ? "text-green-600"
                : status.includes("⚠️")
                ? "text-yellow-600"
                : "text-red-600"
            }`}
          >
            {status}
          </p>
        )}

        {/* Helper Text */}
        <p className="mt-4 text-sm text-gray-600 text-center">
          Ensure your face is clearly visible for successful attendance.
        </p>
      </div>
    </div>
  );
}
