import axios from "axios";
import { useEffect, useState } from "react";

function Profile() {
  const [data, setData] = useState({});
  const [selectedImage, setSelectedImage] = useState(null);
  const [imageFile, setImageFile] = useState(null);
  const [uploadMessage, setUploadMessage] = useState("");

  useEffect(() => {
    const email = localStorage.getItem("email");

    axios.get("http://127.0.0.1:8000/api/myprofile/", {
      params: { email }
    })
    .then(res => setData(res.data))
    .catch(err => console.error(err));
  }, []);

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    setImageFile(file);

    if (file) {
      setSelectedImage(URL.createObjectURL(file));
    }
  };

  const uploadPhoto = async () => {
    if (!imageFile) {
      setUploadMessage("Please select a PNG/JPG photo first!");
      return;
    }

    const formData = new FormData();
    formData.append("email", data.email);
    formData.append("photo", imageFile);

    setUploadMessage("Uploading...");

    try {
      const res = await axios.post(
        "http://127.0.0.1:8000/api/upload-photo/",
        formData,
        { headers: { "Content-Type": "multipart/form-data" } }
      );

      setUploadMessage(res.data.status);
    } catch (error) {
      setUploadMessage("Upload failed. Try again.");
    }
  };

  return (
    <div className="p-4">
      <h2 className="text-xl font-bold">My Profile</h2>

      <table className="table table-striped mt-3 w-1/2 border">
        <tbody>
          <tr>
            <th>Name</th>
            <td>{data.name}</td>
          </tr>

          <tr>
            <th>Email</th>
            <td>{data.email}</td>
          </tr>

          <tr>
            <th>Department</th>
            <td>{data.department}</td>
          </tr>

          <tr>
            <th>Profile Photo</th>
            <td>
              {data.photo ? (
                <img
                  src={`http://127.0.0.1:8000${data.photo}`}
                  alt="profile"
                  className="w-32 h-32 object-cover rounded border"
                />
              ) : (
                "No photo uploaded"
              )}
            </td>
          </tr>
        </tbody>
      </table>

      <div className="mt-5 p-4 border rounded w-1/2">
        <h3 className="font-semibold">Upload New Photo (PNG/JPG)</h3>

        <input
          type="file"
          accept="image/png, image/jpeg"
          onChange={handleImageChange}
          className="mt-2"
        />

        {selectedImage && (
          <div className="mt-3">
            <p className="font-medium">Preview:</p>
            <img
              src={selectedImage}
              alt="preview"
              className="w-40 h-40 object-cover border rounded"
            />
          </div>
        )}

        <button
          onClick={uploadPhoto}
          className="btn btn-primary mt-3"
        >
          Upload & Save
        </button>

        {uploadMessage && (
          <p className="mt-3 text-blue-600 font-medium">{uploadMessage}</p>
        )}
      </div>
    </div>
  );
}

export default Profile;
